import React from 'react';
import * as utils from './functions.js';


class Board extends React.Component {
    constructor(props) {
        super(props);
        this.state = {board: Array(9).fill(null)};
    }

    renderSquare(i) {
        return <utils.square value={this.state.square[i]} onClick={() => this.handleClick(i)}/>
    }

  render() {
    return (
    <div>
        <div className="board-row">
          {this.renderSquare(0)}
          {this.renderSquare(1)}
          {this.renderSquare(2)}
        </div>
        <div className="board-row">
          {this.renderSquare(3)}
          {this.renderSquare(4)}
          {this.renderSquare(5)}
        </div>
        <div className="board-row">
          {this.renderSquare(6)}
          {this.renderSquare(7)}
          {this.renderSquare(8)}
        </div>
    </div>
    )
  }
}

export default Board;
