import React from 'react';
import axios from 'axios';

class Board extends React.Component {
    constructor(props) {
        super(props);
        this.state = {squares: Array(9).fill(null), fc: 0};
    }

    componentDidMount() {
        this.play()
    }

    play() {
        this.setState({squares: Array(9).fill(null)})
        this.emptyIndexies(this.state.squares);
