        if (this.state.isNext === "X") {
            console.log(this.minimax(this.state.squares, aiPlayer))
        }
    }

    renderSquare(i) {
        return <button onClick={() => this.handlePick(i)}>{this.state.squares[i]}</button>
    }

    handlePick(i) {
        const squares = this.state.squares;
        console.log(squares)
		if (this.winner(squares)) {
            console.log("winner")
            return;
		}

		if(this.full(squares) === "X" || this.full(squares) === "O" ) {
            console.log("full")
			return;
        }


        var aiPlayer = "X";
        var huPlayer = "O";
		squares[i] = this.state.isNext ? aiPlayer : huPlayer;
		this.setState({
			squares: squares,
			isNext: !this.state.isNext
        });
        console.log("update")
    }

    minimax(newBoard, player){
        if (this.winner()) {
            return;
        } else if(!this.winner && this.isFilled) {
            return;
        }
        //add one to function calls
        this.setState({fc: this.state.fc + 1});
        console.log(this.state.fc);
        //available spots
        var availSpots = this.emptyIndexies(newBoard);
        var huPlayer = "O";
        var aiPlayer = "X";
        // checks for the terminal states such as win, lose, and tie and returning a value accordingly
        if (this.winning(newBoard, huPlayer)){
           return {score:-10};
        }
          else if (this.winning(newBoard, aiPlayer)){
          return {score:10};
          }
        else if (availSpots.length === 0){
            return {score:0};
        }

        // an array to collect all the objects
        var moves = [];

        // loop through available spots
        for (var i = 0; i < availSpots.length; i++){
            //create an object for each and store the index of that spot that was stored as a number in the object's index key
            var move = {};
            newBoard[availSpots[i]] = player;
            if (player === aiPlayer){
                move.score = this.minimax(newBoard, huPlayer).score;
              }
              else{
                move.score = this.minimax(newBoard, aiPlayer).score;
              }

            newBoard[availSpots[i]] = move.index;

            // push the object to the array
            return move.index
        }
    }

    full(squares) {
        let count = 0;
        squares.forEach(function (item) {
            if (item !== null) {
                count++;
            }
        });
        if(count === 9) {
            return true;
        } else {
            return false;
        }
    }

    winning(board, player) {
        if (
            (board[0] === player && board[1] === player && board[2] === player) ||
            (board[3] === player && board[4] === player && board[5] === player) ||
            (board[6] === player && board[7] === player && board[8] === player) ||
            (board[0] === player && board[3] === player && board[6] === player) ||
            (board[1] === player && board[4] === player && board[7] === player) ||
            (board[2] === player && board[5] === player && board[8] === player) ||
            (board[0] === player && board[4] === player && board[8] === player) ||
            (board[2] === player && board[4] === player && board[6] === player)
            ) {
            return true;
        } else {
            return false;
        }
    }

    winner(squares) {
        const win_combination = [
            [0, 1, 2],
            [3, 4, 5],
            [6, 7, 8],
            [0, 3, 6],
            [1, 4, 7],
            [2, 5, 8],
            [0, 4, 8],
            [2, 4, 6],
        ];

        for (let i = 0; i < win_combination.length; i++) {
            const [a, b, c] = win_combination[i];
            if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
                if (squares[a] === "O") {
                    axios.get(`http://127.0.0.1:9393/increaseWins?name=${this.state.inputName}`);
                    console.log(squares[a]);
                    this.play()
                } else {
                    axios.get(`http://127.0.0.1:9393/increaseWins?name=AI`);
                    console.log(squares[a]);
                    this.play()
                }
                return squares[a];
            }
        }

        return null;
    }

    emptyIndexies(board){
        return board.filter(s => s != "O" && s != "X");
    }

  render() {
    var aiPlayer = "X";
    const winner = this.winner(this.state.squares);
    const isFilled = this.full(this.state.squares);
    let status;
    if (winner) {
        status = 'The winner is: ' + winner;
    } else if(!winner && isFilled) {
        status = 'Game drawn';
    } else {
        status = 'Now ' + (this.state.isNext ? 'X' : 'O') + '\'s turn';
    }

    return (
    <div className="container">
        <h1 className="status">{status}</h1>
        <div className="board-row">
          {this.renderSquare(0)}
          {this.renderSquare(1)}
          {this.renderSquare(2)}
        </div>
        <div className="board-row">
          {this.renderSquare(3)}
          {this.renderSquare(4)}
          {this.renderSquare(5)}
        </div>
        <div className="board-row">
          {this.renderSquare(6)}
          {this.renderSquare(7)}
          {this.renderSquare(8)}
        </div>
        <form action={this.emptyIndexies(this.state.squares)}>
            <input type="submit" value="New game"/>
        </form>
    </div>
    )
  }
}

export default Board;
