import React from 'react';
import axios from 'axios';
import './App.css';
import Board from './components/board'

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {inputName1: "", inputName2: "", player1: nill, player2: null, loses: null};
  }

  componentDidMount() {
    var name1 = prompt("What is player 1's name?");
    this.setState({inputName1: name1})
    var name2 = prompt("What is player 2's name?");
    this.setState({inputName2: name2})
    this.getPlayer(name1, 1);
    this.getPlayer(name2, 2);
  }

  getPlayer(name, player) {
    if (player === 1) {
      axios.get(`http://127.0.0.1:9393/userStats?name=${name}`)
      .then(res => {
        var result = res.data
        console.log(result)
        this.setState({name: result[0], wins: result[1], loses: result[2]})
      })
    }
    if (player === 2) {
      axios.get(`http://127.0.0.1:9393/userStats?name=${name}`)
      .then(res => {
        var result = res.data
        console.log(result)
        this.setState({name: result[0], wins: result[1], loses: result[2]})
      })
    }
  } 

  render() {
    return (
      <div>
        <Board player1={this.state.inputName1} player2={this.state.inputName2} />
        <p>{this.state.name}</p>
        <p>{this.state.wins}</p>
        <p>{this.state.loses}</p>
      </div>
    )
  }
}

export default App;
