import React from 'react';
import * as utils from './functions.js';


class Board extends React.Component {
    constructor(props) {
        super(props);
        this.state = {board: Array(9).fill(null)};
    }

    renderSquares(i) {
        return <utlis value={this.state.squares[i]} onClick={() => this.handleClick(i)}/>
    }

  render() {
    return (
      <ul>
          <li> {this.state.board[0]} | {this.state.board[1]} | {this.state.board[2]}</li>
          <li> --------- </li>
          <li> {this.state.board[3]} | {this.state.board[4]} | {this.state.board[5]}</li>
          <li> --------- </li>
          <li> {this.state.board[6]} | {this.state.board[7]} | {this.state.board[8]}</li>
      </ul>
    )
  }
}

export default Board;
