import React from 'react';
import axios from 'axios';

class Board extends React.Component {
    constructor(props) {
        super(props);
        this.state = {squares: Array(9).fill(null)};
    }

    renderSquare(i) {
        return <button onClick={() => this.handlePick(i)}>{this.state.squares[i]}</button>
    }

    handlePick(i) {
        const squares = this.state.squares.slice();
		if (this.winner(squares) || squares[i]) {
            return;
		}

		if(this.full(squares) === true) {
			return;
		}

		squares[i] = this.state.isNext ? 'X' : 'O';
		this.setState({
			squares: squares,
			isNext: !this.state.isNext
		});
    }



    full(squares) {
        let count = 0;
        squares.forEach(function (item) {
            if (item !== null) {
                count++;
            }
        });
        if(count === 9) {
            return true;
        } else {
            return false;
        }
    }

    restart() {
        this.squares = Array(9).fill(null);
    }


  render() {
    const winner = this.winner(this.state.squares);
    const isFilled = this.full(this.state.squares);
    let status;
    if (winner) {
        status = 'The winner is: ' + winner;
    } else if(!winner && isFilled) {
        status = 'Game drawn';
    } else {
        status = 'Now ' + (this.state.isNext ? 'X' : 'O') + '\'s turn';
    }

    return (
    <div className="container">
        <h1 className="status">{status}</h1>
        <div className="board-row">
          {this.renderSquare(0)}
          {this.renderSquare(1)}
          {this.renderSquare(2)}
        </div>
        <div className="board-row">
          {this.renderSquare(3)}
          {this.renderSquare(4)}
          {this.renderSquare(5)}
        </div>
        <div className="board-row">
          {this.renderSquare(6)}
          {this.renderSquare(7)}
          {this.renderSquare(8)}
        </div>
        <form action={this.restart()}>
            <input type="submit" value="New game"/>
        </form>
    </div>
    )
  }
}

export default Board;
